//       { path: '/products', element: <ProductsPage />, },
//       { path: '/products/:productId', element: <ProductDetailPage /> }
//     ]
//   },

// ])

function App() {
  return <RouterProvider store={route}><div>Home</div></RouterProvider>;
}

export default App;
